(ns rosalind.dna-test
  (:require [clojure.test :refer :all]
            [rosalind.dna :refer :all]))

(deftest test-count-nucleotides)
  (is (= [20 12 17 21]
          (count-nucleotides "AGCTTTTCATTCTGACTGCAACGGGCAATATGTCTCTGTGTGGATTAAAAAAAGAGTGTCTGATAGCAGC")))
  (is (= [205 200 215 214]
         (count-nucleotides "")))
