(ns rosalind.dna-to-rna-test
  (:require [clojure.test :refer :all]
            [rosalind.dna-to-rna :refer :all]))

(deftest test-dna-to-rna []
  (is (= "GAUGGAACUUGACUACGUAAAUU"
         (dna-to-rna "GATGGAACTTGACTACGTAAATT")))
  (is (= ""
         (dna-to-rna "")))
  (is (= ""
         (dna-to-rna "")))
  (is (= ""
         (dna-to-rna ""))))
